    private void checkButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_checkButtonActionPerformed
        recRevOut.setText(null);
        saleRevOut.setText(null);
        if(mlm.getGenerationRecruitRevenue((int)genCounter.getValue(),mlm.getRoot(),0.0)==0)
            JOptionPane.showMessageDialog(this,"Generation does not exist.","Error",JOptionPane.ERROR_MESSAGE);
        else{
            recRevOut.setText(Double.toString(mlm.getGenerationRecruitRevenue((int)genCounter.getValue(),mlm.getRoot(),0.0)));
            saleRevOut.setText(Double.toString(mlm.getGenerationSalesRevenue((int)genCounter.getValue(),mlm.getRoot(),0.0)));
        }
    }//GEN-LAST:event_checkButtonActionPerformed

